mapper.select:
select * from sys_menu

	1=1
	
		and pid = #{pid}
	
	
		and type = #{type}
	
	order by pid,order_no

select * from sys_menu

	id != 1
	
		and pid = #{pid}
	
	
		and type = #{type}
	
	
		
		and menu_name like #{pattern}
	
	order by pid,order_no

mapper.delete:
delete from sys_menu
where 

	
		id = #{array[0]}
	
	
		id in
		
			#{item}
