mapper.select:
select * from sys_role where 1=1
and role_name like #{pattern}
order by create_time desc

mapper.delete:
  	delete from sys_role 
  	where
  	
		
			role_id = #{array[0]}
		
		
			role_id in
			
				#{item}

mapper.select:
select b.id,pid,menu_name as name from sys_role_permission a,sys_menu b 
where a.permission_id = b.id
and a.role_id = #{roleId}

select t.permission_id,b.id,b.pid,b.menu_name as name 
from (
		select * from sys_role_permission a 
		where a.role_id=#{roleId}
	) t
right join 
sys_menu b 
on t.permission_id = b.id
